        $sign  = \'\';\n        foreach ($parameter AS $key => $val)\n        {\n            $param .= \"$key=\" .urlencode($val). \"&\";\n            $sign  .= \"$key=$val&\";\n        }\n		\n        $param = substr($param, 0, -1);\n        $sign  = substr($sign, 0, -1).$alipay_key;\n        $button = \"https://www.alipay.com/cooperate/gateway.do?\".$param.\"&sign=\".md5($sign).\"&sign_type=MD5\";\n        return $button;\n    }\n function respond($db)\n    {\n        /*\n        if (!empty($_POST))\n        {\n            foreach($_POST as $key => $data)\n            {\n                $_GET[$key] = $data;\n            }\n        }\n        */\n        $payment = $db->Payment->findbycode(\'alipay\');\n        eval($payment[\'Payment\'][\'config\']);    \n		$alipay_account = $payment_arr[\'alipay_account\'][\'value\'];\n		$alipay_partner = $payment_arr[\'alipay_partner\'][\'value\'];\n		$alipay_key = $payment_arr[\'alipay_key\'][\'value\'];\n        $seller_email = rawurldecode($_GET[\'seller_email\']);\n        $order_id = str_replace($_GET[\'subject\'], \'\', $_GET[\'out_trade_no\']);\n        $order_id = trim($order_id);\n        //取到的是支付的记录id\n        /* 检查支付的金额是否相符 */\n        $payment_log = $db->PaymentApiLog->findbyid($order_id);\n		\n		//检查是否已成功支付\n		if($payment_log[\'PaymentApiLog\'][\'is_paid\'] == \"1\"){\n 			return false;\n 		}\n		\n		if($payment_log[\'PaymentApiLog\'][\'type\'] > 0){\n			$account = $db->UserAccount->findbyid($payment_log[\'PaymentApiLog\'][\'type_id\']);\n	        if ($_GET[\'total_fee\'] != $account[\'UserAccount\'][\'amount\'])\n	        {\n	            return false;\n	        }\n        }else{\n        $order_total = $db->Order->findbyid($payment_log[\'PaymentApiLog\'][\'type_id\']);\n	        if ($_GET[\'total_fee\'] != $order_total[\'Order\'][\'total\'])\n	        {\n	            return false;\n	        }\n        }\n\n		$charset = \'utf-8\';\n		$service = \'trade_create_by_buyer\';\n		$agent = \'C4335319945672464113\';\n		$host = isset($_SERVER[\'HTTP_X_FORWARDED_HOST\']) ? $_SERVER[\'HTTP_X_FORWARDED_HOST\'] : (isset($_SERVER[\'HTTP_HOST\']) ? $_SERVER[\'HTTP_HOST\'] : \'\');\n        \n        ksort($_GET);\n        reset($_GET);\n        $arg = \"\";\n		while (list ($key, $val) = each ($_GET)) {\n			if($key != \"sign\" && $key != \"sign_type\" && $key != \'code\' && $key != \'url\')\n			$arg.=$key.\"=\".$val.\"&\";\n		}\n		$prestr = substr($arg,0,count($arg)-2);  //去掉最后一个&号\n		 $sign = $prestr.$alipay_key;\n\n      if (md5($sign) != $_GET[\'sign\'])\n        {\n            return false;\n        }\n		if($payment_log[\'PaymentApiLog\'][\'type\'] > 0){\n			/* 充值成功*/\n			$db->update_balance($payment_log,$account);        			           \n			return true;\n		\n		}else if ($_GET[\'trade_status\'] == \'WAIT_SELLER_SEND_GOODS\')\n        {\n			$db->update_order($payment_log,$order_total);        			           \n			 return true;\n        }else if ($_GET[\'trade_status\'] == \'TRADE_FINISHED\')\n        {\n			$db->update_order($payment_log,$order_total);       \n            return true;\n        }\n        else\n        {   \n            return false;\n        }\n    }\n}', '', '2008-01-01 00:00:00', '2018-07-31 06:53:59'),
